import axios from "axios"

function UpdateProduct() {
  const product = state?.book;
   const [products,setproducts]=useState([])
  const [name,setName]=useState(book?.author || '')
      const [imagUrl,setimageUrl]=useState(products.imagUrl)
      const [price,setPrice]=useState(products.price)
      const [description,setdescription]=useState(products.description)
      const updateProduct=(id,obj)=>{
        axios.put(`http://localhost:2200/api/product/uptade/${id}`,obj)
        .then(()=>{
          fetch()
        }).catch((error)=>{
            throw error
        })
      
      }
    return (
        <form>
    <div className="mb-3">
      <label for="exampleInputEmail1" className="form-label">name</label>
      <input type="text" className="form-control" onChange={(e)=>setName(e.target.value)} defaultValue={name} id="exampleInputEmail1" aria-describedby="emailHelp"/>
    </div>
    <div className="mb-3">
      <label for="exampleInputPassword1" className="form-label">image</label>
      <input type="text" className="form-control" onChange={(e)=>setimageUrl(e.target.value)} defaultValue={imagUrl}  id="exampleInputPassword1"/>
    </div>
    <div className="mb-3">
      <label for="exampleInputPassword1" className="form-label">description</label>
      <input type="text" className="form-control" onChange={(e)=>setdescription(e.target.value)} defaultValue={description}  id="exampleInputPassword1"/>
    </div>
    <div className="mb-3">
      <label for="exampleInputEmail1" className="form-label">price</label>
      <input type="text" className="form-control" onChange={(e)=>setPrice(e.target.value)} defaultValue={price}  id="exampleInputEmail1" aria-describedby="emailHelp"/>
    </div>
    
    <button type="button" className="btn btn-primary"  >update</button>
  </form>
    )
  }


export default UpdateProduct
