import "./App.css";
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import ProductList from "./components/productlist";
import AddProduct from "./components/addproduct";
import UpdateProduct from "./components/updateproduct";
import { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [products,setproducts]=useState([])

const fetch=()=>{
  axios.get("http://localhost:2200/api/product/getall")
.then((response)=>{
  setproducts(response.data)
  console.log(response.data)
}).catch((error)=>{
  throw error
})
}
useEffect(()=>{
  fetch()
},[])

const addproduct=(obj)=>{
  axios.post("http://localhost:2200/api/product/add",obj)
  .then(()=>{
    fetch()
  }).catch((error)=>{
    throw error
  })
}
const deleteproduct=(id)=>{
  axios.delete(`http://localhost:2200/api/product/delete/${id}`)
  .then(()=>{
    fetch()
  })
  .catch((error)=>{
    throw error
  })
}
const updateProduct=(id,obj)=>{
  axios.put(`http://localhost:2200/api/product/uptade/${id}`,obj)
  .then(()=>{
    fetch()
  }).catch((error)=>{
      throw error
  })

}

  return <div>
    <Router>
   <Routes>
          <Route path="/" element={<ProductList/>} />
          <Route path="/add" element={<AddProduct/>} />
          <Route path="/update" element={<UpdateProduct/>}/>
          
      </Routes>
      </Router>
  </div>;
}

export default App;
