import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';
import axios from "axios";

function ProductList({ products, fetch }) {
  const [searchQuery, setSearchQuery] = useState(""); // State to store the search query
  const navigate = useNavigate();

  const deleteproduct = (id) => {
    axios.delete(`http://localhost:2200/api/product/delete/${id}`)
      .then(() => {
        fetch();
      })
      .catch((error) => {
        throw error;
      });
  };

  // Function to handle the search
  const handleSearch = () => {
    if (searchQuery.trim()) {

      const filteredProducts = products.filter((product) =>
        product.name.includes(searchQuery)
      );
      return filteredProducts;
    } else {
      return products; 
    }
  };

  const CategoryFilter = () => {
    const [categories, setCategories] = useState([]);
    const fetchCategories = async (filter = '') => {
      const response = await fetch(`http://localhost:2200/api/category${filter}`);
      const data = await response.json();
      setCategories(data)
    }
   useEffect(() => {
      fetchCategories();
    }, []);
  
  };

  return (
    <div>
      <nav className="navbar navbar-expand-lg bg-body-tertiary">
        <div className="container-fluid">
          <a className="navbar-brand" href="#">Dour/Echri</a>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <a className="nav-link active" aria-current="page" href="#">Home</a>
              </li>
              <li className="nav-item">
                <a className="nav-link active" aria-current="page" href="#" onClick={() => navigate("/add")}>Add Products</a>
              </li>
              <li className="nav-item dropdown">
                <a className="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                  C-tegory
                </a>
                <ul className="dropdown-menu">
                  <li><a className="dropdown-item" href="#">Véhicules</a></li>
                  <li><a className="dropdown-item" href="#">Informatique et Multimedias</a></li>
                  <li><a className="dropdown-item" href="#">Loisirs et Divertissement</a></li>
                </ul>
              </li>
            </ul>
            <form className="d-flex" role="search">
              <input
                className="form-control me-2"
                type="search"
                placeholder="Search by name"
                aria-label="Search"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)} 
              />
              <button className="btn btn-outline-success" type="button" onClick={() => { handleSearch() }}>Search</button>
            </form>
          </div>
        </div>
      </nav>

      {handleSearch().map((el, index) => (
        <div className="card" style={{ width: "18rem" }} key={index}>
          <img src="..." className="card-img-top" alt="..." />
          <div className="card-body">
            <h5 className="card-title">{el.name}</h5>
            <p className="card-text">{el.imageUrl}</p>
            <p className="card-text">{el.description}</p>
            <a href="#" className="btn btn-primary">{el.price}</a>
            <button onClick={() => { deleteproduct(el.id) }}>Delete</button>
            <button onClick={() => navigate("/update", { state: { product: el } })}>Update</button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default ProductList;
