import React,{useState, useEffect} from "react";
import {useNavigate } from 'react-router-dom';

function ProductList(){
  
   const navigate=useNavigate()
   const [products,setproducts]=useState([])

   const fetch=()=>{
     axios.get("http://localhost:2200/api/product/getall")
   .then((response)=>{
     console.log(response.data)
     setproducts(response.data)
   }).catch((error)=>{
     throw error
   })
   }
   useEffect(()=>{
     fetch()
   },[])
   
return( 
    <div>
    {products.map((el,index)=>{
      return  <div className="card" style={{width: "18rem"}} key={index}>
      <img src="..." className="card-img-top" alt="..."/>
      <div className="card-body">
        <h5 className="card-title">{el.name}</h5>
        <p className="card-text">{el.imageUrl}</p>
        <p className="card-text">{el.description}</p>
        <a href="#" className="btn btn-primary">{el.price}</a>
        {/* <button onClick={()=>{deleteproduct(el.id)}}>Delete</button> */}
        <button onClick={()=>navigate("/update")}>update</button>
        {/* {show&&<UpdateProduct product={el} updateProduct={updateProduct} changeShow={changeShow}/>} */}
        </div>    </div>
    })}    </div>
        )}
export default ProductList
