import { useEffect, useState } from "react";
import axios from 'axios'
import { useNavigate } from 'react-router-dom';

function AddProduct() {
    const navigate = useNavigate();
    const [products,setproducts]=useState([])

    const [name,setName]=useState("")
    const [imagUrl,setimageUrl]=useState("")
    const [description,setdescription]=useState("")
    const [price,setPrice]=useState("")
    const addproduct=(obj)=>{
      axios.post("http://localhost:2200/api/product/add",obj)
      .then(() => {
    
        navigate("/list")
      })
      .catch((error) => {
        throw error;
      });
    }
  return (
      <form>
  <div className="mb-3">
    <label for="exampleInputEmail1" className="form-label">name</label>
    <input type="text" className="form-control" onChange={(e)=>setName(e.target.value)} placeholder={name} id="exampleInputEmail1" aria-describedby="emailHelp"/>
  </div>
  <div className="mb-3">
    <label for="exampleInputPassword1" className="form-label">image</label>
    <input type="text" className="form-control" onChange={(e)=>setimageUrl(e.target.value)} placeholder={imagUrl}  id="exampleInputPassword1"/>
  </div>
  <div className="mb-3">
    <label for="exampleInputPassword1" className="form-label">description</label>
    <input type="text" className="form-control" onChange={(e)=>setdescription(e.target.value)} placeholder={description}  id="exampleInputPassword1"/>
  </div>
  <div className="mb-3">
    <label for="exampleInputEmail1" className="form-label">price</label>
    <input type="text" className="form-control" onChange={(e)=>setPrice(e.target.value)} placeholder={price}  id="exampleInputEmail1" aria-describedby="emailHelp"/>
  </div>
  
  <button type="button" className="btn btn-primary" onClick={()=>{addproduct({name,imagUrl,price,description})}}>add</button>
</form>
  )
}

export default AddProduct
